import { describe, it, expect, beforeAll, afterAll } from "vitest";
import { Opik } from "@/index";
import { evaluatePrompt } from "@/evaluation/evaluatePrompt";
import type { OpikMessage } from "@/evaluation/models";
import { Contains } from "@/evaluation/metrics/heuristics/Contains";
import {
  getIntegrationTestStatus,
  shouldRunIntegrationTests,
} from "../api/shouldRunIntegrationTests";
import { cleanupDatasets, cleanupPrompts } from "./helpers/testData";

const CAT_IMAGE_URL =
  "https://upload.wikimedia.org/wikipedia/commons/3/3a/Cat03.jpg";
const DOG_IMAGE_DATA_URL =
  "data:image/png;base64,";
const FOX_IMAGE_DATA_URL =
  "data:image/jpeg;base64,";

const MULTIMODAL_MESSAGES: OpikMessage[] = [
  {
    role: "system",
    content:
      "You are an image classifier. Each prompt may contain one or two images. If only the first image has content, answer with a single lowercase word. If both images have content, answer with two lowercase words separated by a single space in the order the images are provided.",
  },
  {
    role: "user",
    content: [
      {
        type: "text",
        text: "Classify the animal(s) shown. Follow the instruction about how many words to use.",
      },
      { type: "image_url", image_url: { url: "{{image_url}}" } },
      { type: "image_url", image_url: { url: "{{secondary_image_url}}" } },
    ],
  },
];

const shouldRunApiTests = shouldRunIntegrationTests();

describe.skipIf(!shouldRunApiTests)(
  "evaluatePrompt multimodal integration",
  () => {
    let client: Opik;
    const createdDatasetNames: string[] = [];
    const createdPromptIds: string[] = [];

    beforeAll(() => {
      console.log(getIntegrationTestStatus());

      if (!shouldRunApiTests) {
        return;
      }

      client = new Opik();
    });

    afterAll(async () => {
      if (client) {
        await client.flush();
      }

      await cleanupDatasets(client, createdDatasetNames);
      await cleanupPrompts(client, createdPromptIds);
    });

    it(
      "should evaluate prompts with image inputs",
      async () => {
        const datasetName = `test-multimodal-dataset-${Date.now()}`;
        const dataset = await client.createDataset(datasetName);
        createdDatasetNames.push(dataset.name);

        await client.datasetBatchQueue.flush();

        await dataset.insert([
          {
            prompt: "Identify the animal in the first image.",
            image_url: CAT_IMAGE_URL,
            secondary_image_url: "",
            reference: "cat",
          },
          {
            prompt: "Identify the animal in the first image.",
            image_url: DOG_IMAGE_DATA_URL,
            secondary_image_url: "",
            reference: "dog",
          },
          {
            prompt: "Identify the animal in the first image.",
            image_url: FOX_IMAGE_DATA_URL,
            secondary_image_url: "",
            reference: "fox",
          },
          {
            prompt: "Identify the animals in both images.",
            image_url: DOG_IMAGE_DATA_URL,
            secondary_image_url: FOX_IMAGE_DATA_URL,
            reference: "dog fox",
          },
          {
            prompt: "Identify the animals in both images.",
            image_url: CAT_IMAGE_URL,
            secondary_image_url: CAT_IMAGE_URL,
            reference: "cat cat",
          },
        ]);

        const result = await evaluatePrompt({
          dataset,
          messages: MULTIMODAL_MESSAGES,
          scoringMetrics: [new Contains("contains", true)],
          scoringKeyMapping: {
            substring: "reference",
          },
          experimentName: `test-multimodal-${Date.now()}`,
          model: "gpt-5-mini",
        });

        expect(result.testResults.length).toBe(5);
        result.testResults.forEach((testResult) => {
          expect(testResult.scoreResults).toHaveLength(1);
          expect(testResult.scoreResults[0]?.value).toBe(1);
        });
      },
      180_000
    );
  }
);
